                class PlayList{
                    // Atributos
                    private $name;
                    private $artist = array(); 
                    private $album = array();
                    private $cover = array();  
                    private $year = array(); 

                    // Methods
                    public function __construct($name){
                        $this->name = $name;
                        // $this->artist = $artist;
                        // $this->album = $album;
                        // $this->cover = $cover;
                        // $this->year = $year;
                    }
                    public function setPlayList($artist,$album,$cover,$year){
                        $this->artist[] = $artist;
                        $this->album[] = $album;
                        $this->cover[] = $cover;
                        $this->year[] = $year;

                    }
                    public function getPlayList(){
                        echo "<div class='p-4 flex flex-col grap-2 items-center border-2 right-1 ring-white/50 rounded-1g w-[300px] m-4'>
                            <h2 class='text-center'>Play List: $this->name</h2>";

                            for($i = 0; $i < count($this->artist);$i++){    
                            echo "<div class='bg-white/20 p-4 flex flex-col grap-2 rounded-lg items-center border-2 right-1 ring-white/50 rounded-1g w-[250px] m-4 '>
                            <img class='rounded-full' src='".$this->cover[$i]."' alt='Album Cover'>
                            <p><strong>Album: </strong>".$this->album[$i]."</p>
                            <p><strong>Artist: </strong>".$this->artist[$i]."</p>
                            <p><strong>Year: </strong>".$this->year[$i]."</p>
                            </div>";
                            } 
                        echo "</div>";
                    }


                }
